    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       try{
        SimpleDateFormat d = new SimpleDateFormat("dd-MM-yyyy");
        String inicio = d.format(txtInicial.getDate());
        String fim =  d.format(txtFinal.getDate());
        TransacaoDAO.retornaTransacao(Login.lconta,inicio,fim);
        DefaultListModel d1m = new DefaultListModel();
        for (Object a: Login.lconta.extrato){
            d1m.addElement(a);
            Jextrato.setModel(d1m);
        }
        Login.lconta.extrato.clear();
       }
        catch(NullPointerException e){
                JOptionPane.showMessageDialog(null, "Insira uma data");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

